lib.base64:
exports.btoa = function(string) {
        var b64 = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789-_";
        var bitmap, a, b, c,
            result = "", i = 0,
            rest = string.length % 3; 

        for (; i < string.length;) {
            if ((a = string.charCodeAt(i++)) > 255
                    || (b = string.charCodeAt(i++)) > 255
                    || (c = string.charCodeAt(i++)) > 255)
                throw new TypeError("Failed to execute 'btoa' on 'Window': The string to be encoded contains characters outside of the Latin1 range.");

            bitmap = (a << 16) | (b << 8) | c;
            result += b64.charAt(bitmap >> 18 & 63) + b64.charAt(bitmap >> 12 & 63)
                    + b64.charAt(bitmap >> 6 & 63) + b64.charAt(bitmap & 63);
        }

        return rest ? result.slice(0, rest - 3) : result;
    };

lib.hex:
exports.hexToBase64 = function(str) {
  var btoa = require("views/lib/base64").btoa;
  return btoa(String.fromCharCode.apply(null,
    str.replace(/\r|\n/g, "").replace(/([\da-fA-F]{2}) ?/g, "0x$1 ").replace(/ +$/, "").split(" "))
  );
}

stats.map:
function (doc) {
  var t = ((doc.time/86400000)|0);
  if (doc._id.substr(0,6)=="trade.") {
   emit([doc.ident,t,doc.currency],doc.change);
   if (doc.btc) emit([doc.ident,t,"_b"],doc.btc);
  } else if (doc._id.substr(0,8)=="deposit.") {
    emit([doc.ident,t,"_f"],doc.change);
    emit([doc.ident,t,"_d"],doc.change);
  }
}

updates.sendItem:
function(doc, req) {
    var hexToBase64 = require("views/lib/hex").hexToBase64;
    var uid = "trade."+hexToBase64(req.uuid);
    var data = JSON.parse(req.body);
    data._id = uid;
    if (doc && doc.rates && data.change) {
        var cur = data.currency.toUpperCase();
        var rate = doc.rates[cur];
        if (rate) {
            data.btc = data.change*rate;
        }
    }
    return [data, data._id];
}

updates.updateRates:
function(doc, req) {
    if (doc == null) throw "invalid request";
    var data = JSON.parse(req.body);
    var c = data["crypto"];
    var f = data["forex"];
    if (c) {
        var r = c.result;
        r = r.rows;
        r.forEach(function(x) {
            var s = x.symbol.toUpperCase();
            doc.rates[s] = parseFloat(x.price);
        });
        doc.rates["USD"] = doc.rates["USDT"];
    }
    var usdrate = doc.rates["USD"];
    if (f) {
        for (var k in f) {
            var x = f[k];
            if (x.type == "Forex") {
                var a = x.marginCurrency;
                var c = x.priceCurrency;
                var r = (x.quote.a + x.quote.b)*0.5;
                if (c == "USD" && c != a) {
                    doc.rates[a] = r * usdrate;
                } else if (a == "USD" && c != a) {
                    doc.rates[c] = usdrate/r;
                }
            }
        }
    }
    return [doc,"ok"];
}

lists.report:
function(hdr,req) {
    var static = {
        "hdr":["Date","Test"],
        "rows":[[0,1234]],
        "sums":[3232],
        "avg":[111]
        };


    start({
        'headers': {
            'Content-Type': 'application/json'
        }
    });

    var alldata = [];
    var cr = {};
    var rd = 0;

    var sums = {"_b":0,"_f":0,"_d":0};
    var fee = 0.15;

    while (r=getRow()) {
        var t = r.key[1];
        var s = r.key[2];
        var v = r.value;
        if (s == "_%") {
            fee = v;
        } else {
            if (t != rd) {
                if (rd) alldata.push(cr);
                cr = {Date: t*86400};
                rd = t;
            }
            cr[s] = v;
            if (sums[s]) sums[s]+=v; else sums[s]=v;
            if (s == "_b") {
                var z = -v * fee;
                cr["_f"] = z;
                sums["_f"] +=z;
            }
        }

    }
    if (rd) alldata.push(cr);

    var hdrs = Object.keys(sums).sort(function(a,b) {
        if (a[0] == '_' && b[0] != '_') return 1;
        if (a[0] != '_' && b[9] == '_') return -1;
        else return a.localeCompare(b);
    });
    var osums = hdrs.map(function(x) {return sums[x]?sums[x]:0;});
    hdrs.unshift("Date");
    var orows = alldata.map(function(a) {
        return  hdrs.map(function(x) {return a[x]?a[x]:0;});

    });

    hdrs = hdrs.map(function(z) {
        switch(z) {
            case '_b': return "∑BTC";
            case '_f': return "±Fee";
            case '_d': return "Deposit";
            default: return z;
        }
    });

    var result = {
        hdr: hdrs,
        rows: orows,
        sums: osums
    };

    send(JSON.stringify(result));

}
